sysauto_script.script:
//Obtenemos la fecha actual.
var rightNow = new GlideDateTime();
//Preparamos la query pasando de parámetro el nombre de la tabla en la que vamos a operar.
var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
	//Buscamos las que sean mayores o iguales a la fecha actual
	overdueNITasks.addQuery('due_date','>',rightNow);
	//Y que sean menores o iguales a 24 horas despúes de la fecha actual.
	overdueNITasks.addQuery('due_date','<=', gs.hoursAgo(-24));
//El state no puede ser de valor 3
	overdueNITasks.addQuery('state','!=',3);
	overdueNITasks.query();
while(overdueNITasks.next()){
	gs.info("Este registro tiene fecha entre la fecha actual y 24 horas en adelante: " + overdueNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',overdueNITasks,overdueNITasks.number,gs.getUserName());
}
